mapper.update:
UPDATE board SET 
board_content=#{board_content} 
,board_bdate=#{board_bdate}
,board_edate=#{board_edate}
,board_title=#{board_title}
,board_store=#{board_store}
,board_notice=#{board_notice}

,board_file= #{board_file}

where board_number=#{board_number}

mapper.select:
select * from (select board_number, lead(board_number) over(order by board_number) board_next from board where board_deleted='n') where board_number=${board_number}
and board_notice = 'E'
and board_notice = 'N'

select * from (select board_number, lag(board_number) over(order by board_number) board_prev from board where board_deleted='n') where board_number=${board_number}
and board_notice = 'E'
and board_notice = 'N'
